void Bubble_Sort(DataType *arr, int len)
{
	int i, j, temp;

	for (i = 0; i < len - 1; i++)
		for (j = 0; j < len - 1 - i; j++)
			if (arr[j] > arr[j + 1]) {
				temp = arr[j];
				arr[j] = arr[j + 1];
				arr[j + 1] = temp;
			}
}

void Selection_Sort(DataType *arr, int len)
{
	int i, j, k, temp;

	for (i = 0; i < len - 1; i++) {
		k = i;
		for (j = i + 1; j < len; j++)
			if (arr[j] < arr[k])
				k = j;
		if(k != i) {
			temp = arr[k];
			arr[k] = arr[i];
			arr[i] = temp;
		}
	}
}

void Insertion_Sort(DataType *arr, int len)
{
	int i, j, temp;

	for (i = 1; i < len; i++)
		if (arr[i] < arr[i-1]) {
			temp = arr[i];
			for (j = i - 1; j >= 0 && arr[j] > temp; j--)
				arr[j+1] = arr[j];
			arr[j+1] = temp;
		}
}

void Shell_Sort(DataType *arr, int len)
{
	int gap, i, j;
	int temp;

	for (gap = len >> 1; gap > 0; gap >>= 1)
		for (i = gap; i < len; i++) {
			temp = arr[i];
			for (j = i - gap; j >= 0 && arr[j] > temp; j -= gap)
				arr[j + gap] = arr[j];
			arr[j + gap] = temp;
		}
}

#define MIN(x,y)     ((x) < (y) ? (x) : (y))

int Merge_Sort(DataType *arr, int len)
{
	DataType *a = arr;
