	memset(buckets, 0, (n) * sizeof(DataType));

	for (i = 0; i < len; i++)
		buckets[arr[i] - min_val]++;

	for (i = 0, j = 0; i < n; i++)
		while((buckets[i]--) > 0)
			arr[j++] = i + min_val;

	return 0;
}

#define BASE            10

int Radix_Sort(DataType *arr, int len)
{
	DataType *temp,
	         *counts; //计数器
	int i, j, k;
	int radix = 1;

	int max_val = arr[0];

	for(i = 1; i < len; i++)
		if(arr[i] > max_val)
			max_val = arr[i];

	/*为计数器数组分配空间*/
	counts = (DataType *)malloc((BASE+1) * sizeof(DataType));
	/*为已排序元素临时存放数组分配空间*/
	temp = (DataType *)malloc(len * sizeof(DataType));

	if(counts == NULL)
		return -1;

	if(temp == NULL)
		return -1;

	while (max_val / radix > 0) {
		for(j = 0; j < BASE+1; j++)
			counts[j] = 0; // 每次分配前清空计数器

		for(j = 0; j < len; j++) {
			k = (arr[j] / radix) % BASE; // 统计每个桶中的记录数
			counts[k]++;
		}

		for(j = 1; j < BASE+1; j++)
			counts[j] += counts[j - 1]; // 将 temp中的位置依次分配给每个桶

		for(j = len - 1; j >= 0; j--) { // 将所有桶中记录依次收集到 temp中
			k = (arr[j] / radix) % BASE;
			temp[counts[k] - 1] = arr[j];
			counts[k]--;
		}

		for(j = 0; j < len; j++) // 将临时数组的内容复制到 data中
			arr[j] = temp[j];

		radix *= 10;
	}

	/*释放前面分配的空间*/
	free(counts);
	free(temp);

	return 0;
}


